mapper.select:
select

from km_task.task_quartz_set



      and job_id = #{jobId,jdbcType=INTEGER}


      and job_type = #{jobType,jdbcType=VARCHAR}


      and class_type = #{classType,jdbcType=VARCHAR}


      and start_plan_time = #{startPlanTime,jdbcType=TIMESTAMP}


      and next_plan_time = #{nextPlanTime,jdbcType=TIMESTAMP}


      and status = #{status,jdbcType=INTEGER}


      and plan_times = #{planTimes,jdbcType=INTEGER}


      and create_time = #{createTime,jdbcType=TIMESTAMP}


      and cron_id = #{cronId,jdbcType=INTEGER}


      and cron_text = #{cronText,jdbcType=VARCHAR}


      and repository_id = #{repositoryId,jdbcType=INTEGER}
